function getMixin(vm){
    const {title} = vm.$options;
    if(title){
        return typeof title === 'function' ? title.call(vm) : title;
    }
}

// 서버쪽 타이틀 변경
const serverMixin = {
    created(){
        const title = getMixin(this);
        if(title){
            this.$ssrContext.title = title;
        }
    }
}

// 클라이언트 타이틀 변경
const clientMixin = {
    mounted(){
        const title = getMixin(this);
        if(title){
            document.title = title;
        }
    }
}

export default process.env.VUE_ENV === 'server' ? serverMixin : clientMixin;

import Vue from 'vue'
import App from './App.vue'
import { createRouter } from './router'
import { createStore } from './store'
import { sync } from "vuex-router-sync"
import vuetify from './plugins/vuetify'
import './plugins/axios'
import '@babel/polyfill'
import 'roboto-fontface/css/roboto/roboto-fontface.css'
import '@mdi/font/css/materialdesignicons.css'
import titleMixin from './mixins/title-mixin'



Vue.config.productionTip = false

export function createApp(ctx) {

	//console.log('main.js * createApp');

	const router = createRouter();
	const store = createStore();
	sync(store, router);

	Vue.mixin(titleMixin);

	const app = new Vue({
		data: { url: ctx ? ctx.url : '' },
		router,
		store,
		vuetify,
		render: h => h(App)
	});

	return { app, router, store };
}

import { createApp } from './main';

export default (ctx) => {
	return new Promise((resolve, reject) => {
		const { app, router, store } = createApp();

		//console.log('entry-server.js * before router.push')
		router.push(ctx.url);
		//console.log('entry-server.js * after router.push')
		router.onReady(() => {
			//console.log('entry-server.js * onReady')
			ctx.rendered = () => ctx.state = store.state;
			const matchedComponents = router.getMatchedComponents();
			if (matchedComponents.length === 0) {
				return reject({ code: 404 });
			}
			return resolve(app);
		}, reject);
	});
}

